restrictionLayer1.RestrictionStyle = RestrictionStyle.HatchPattern; 
 
RestrictionLayer restrictionLayer2 = new RestrictionLayer(); 
restrictionLayer2.RestrictionStyle = RestrictionStyle.CircleWithSlashImage; 
 
